//@flow


export type Value = {
  value: any,
}

export type NextValue = {
  value: any,
  next?: NextValue,
}

export type FullNode = {
  value: any,
  next?: NextValue,
  back?: NextValue,
}

export type NextFabric = (next?: NextValue) => NextValue
export type FullNodeFabric = (back?: NextValue) => FullNode
